import './App.css';
import { Component } from 'react'
import { CardList } from './components/card-list/card-list.component'

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [],
      searchField: '',
    }
  }

  // component only mount once when placed on the DOM
  //if you want to make api request you needs 
  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json())
    .then((users) => this.setState(
      () => {
      return {monsters: users}
      },
      () => {
        console.log(this.state);
      }
    ));
  }

  // if not seperate, it would be an anonomous function, gets reinitized over and over again when onChange, slow down speed
  onSearchChange = (event) => {
    console.log(event.target.value);
    const searchField = event.target.value.toLocaleLowerCase();
    this.setState(() => {
      return {searchField: searchField};
    }) 
  }

  render(){
    // destructuring of es6, makes variable shorter
    const { monsters, searchField } = this.state;
    const { onSearchChange } = this;

    const filteredMonsters = monsters.filter((monster) => {
      return monster.name.toLocaleLowerCase().includes(searchField);
    })
    return (
      <div className="App">
        <input className='search-box' type='search' placeholder='search monsters' 
        onChange={onSearchChange}></input>
        {/* {filteredMonsters.map((monster) => {
            return <h1 key={monster.id}>{monster.name}</h1>
        })} */}
        <CardList />
      </div>
    );
  }  
}

export default App;
